dunes-script-module.script:
if (!server){ throw "Server cannot be null" }
var port = "443";
if (server.indexOf("http://") != -1){ 
	port = "80";
}
server = server.toLowerCase().replace("https://","").replace("http://","");
if ( server.indexOf(":") != -1) {
	hostname = server.split(":")[0];
	port = server.split(":")[1];
} else {
	hostname = server;
}
return {
	hostname: hostname,
	port: port
};
